"; let sleep = b"GET /sleep HTTP/1.1
"; let (status_line, filename) = if buffer.starts_with(get) { ("HTTP/1.1 200 OK

", "hello.html") } else if buffer.starts_with(sleep) { task::sleep(Duration::from_secs(5)).await; ("HTTP/1.1 200 OK

", "hello.html") } else { ("HTTP/1.1 404 NOT FOUND

", "404.html") }; let contents = fs::read_to_string(filename).unwrap(); let response = format!("{status_line}{contents}"); stream.write(response.as_bytes()).unwrap(); stream.flush().unwrap();
